DROP TABLE IF EXISTS contact CASCADE;
CREATE TABLE contact (
 id SERIAL PRIMARY KEY,
 first_name VARCHAR(64),
 last_name VARCHAR(64),
 company_title VARCHAR(128),
 email VARCHAR(320),
 contact_type VARCHAR(16),
 nation_id INTEGER,
 jurisdiction_id INTEGER
);

ALTER TABLE contact ADD CONSTRAINT contact_nation_id_fkey FOREIGN KEY (nation_id) REFERENCES nation(id);
ALTER TABLE contact ADD CONSTRAINT contact_jurisdiction_id_fkey FOREIGN KEY (jurisdiction_id) REFERENCES jurisdiction(id);

DROP TABLE IF EXISTS partnership CASCADE;
CREATE TABLE partnership (
 id SERIAL PRIMARY KEY,
 nation_id INTEGER,
 funding_amount DECIMAL,
 funding_currency VARCHAR(16),
 initiative_status_id INTEGER,
 url VARCHAR(2083)
);

DROP TABLE IF EXISTS partnership_translate CASCADE;
CREATE TABLE partnership_translate (
 id SERIAL PRIMARY KEY,
 language_code CHAR(2),
 partnership_id INTEGER,
 description TEXT,
 initiative_status_details TEXT,
 name VARCHAR(511)
);

DROP TABLE IF EXISTS organization CASCADE;
CREATE TABLE organization (
 id SERIAL PRIMARY KEY,
 name_short VARCHAR(32),
 url VARCHAR(2083)
);

DROP TABLE IF EXISTS organization_translate CASCADE;
CREATE TABLE organization_translate (
 id SERIAL PRIMARY KEY,
 language_code CHAR(2),
 organization_id INTEGER,
 name_long VARCHAR(255)
);

DROP TABLE IF EXISTS initiative_status CASCADE;
CREATE TABLE initiative_status (
 id SERIAL PRIMARY KEY
);

DROP TABLE IF EXISTS initiative_status_translate CASCADE;
CREATE TABLE initiative_status_translate (
 id SERIAL PRIMARY KEY,
 language_code CHAR(2),
 initiative_status_id INTEGER,
 name VARCHAR(255)
);

DROP TABLE IF EXISTS initiative_type CASCADE;
CREATE TABLE initiative_type (
 id SERIAL PRIMARY KEY
);

DROP TABLE IF EXISTS initiative_type_translate CASCADE;
CREATE TABLE initiative_type_translate (
 id SERIAL PRIMARY KEY,
 language_code CHAR(2),
 initiative_type_id INTEGER,
 name VARCHAR(255)
);

DROP TABLE IF EXISTS jurisdiction_partnership CASCADE;
CREATE TABLE jurisdiction_partnership (
 id SERIAL PRIMARY KEY,
 jurisdiction_id INTEGER,
 partnership_id INTEGER
);

DROP TABLE IF EXISTS organization_partnership CASCADE;
CREATE TABLE organization_partnership (
 id SERIAL PRIMARY KEY,
 organization_id INTEGER,
 partnership_id INTEGER
);

DROP TABLE IF EXISTS partnership_funding_source CASCADE;
CREATE TABLE partnership_funding_source (
  id SERIAL PRIMARY KEY,
  partnership_id INTEGER,
  funding_source_id INTEGER
);

DROP TABLE IF EXISTS partnership_initiative_type CASCADE;
CREATE TABLE partnership_initiative_type (
  id SERIAL PRIMARY KEY,
  partnership_id INTEGER,
  initiative_type_id INTEGER
);


