INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'296',
	'en',
	'296',
	'<p>Control the agricultural frontier, reduce deforestation, and degradation and promote low-emission rural development.</p><p>Reduce GHG emissions</p><p>Reorient forestry and agricultural policy</p><p>Reduce incentives that promote deforestation and degradation</p><p>Increase incentives for conservation, management, restoration and sustainable use of forest resources</p>',
