	'EEREDD+'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'297',
	'en',
	'297',
	'<p>The area of ​​Environmental Services, in favor of the Conservation of natural protected areas, reforested 60 thousand hectares of the marshes of Centla and 56 thousand hectares of the Usumacinta Canyon.</p>',
