INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'544',
	'es',
	'56',
	'<p>Promover el control de la deforestación y mejorar la calidad de vida de las poblaciones tradicionales que viven en áreas protegidas en el estado de Amazonas. El proyecto apoya 16 áreas protegidas (AP) en el estado de Amazonas.</p>',
	null,
	'Programa de Asistencia Forestal'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'545',
	'es',
	'57',
	'<p>Continuar y expandir las acciones del programa Bolsa Floresta en áreas protegidas (AP) en el estado de Amazonas, a través de: (i) el apoyo al desarrollo de pequeñas empresas y acuerdos de producción forestal sostenible; (ii) capacitación de líderes y fortalecimiento de asociaciones locales para la gestión de proyectos con énfasis en la generación de ingresos y preocupaciones ambientales y sociales; (iii) sistematización y difusión de contenidos, metodologías, lecciones aprendidas y soluciones innovadoras; y (iv) ejecución e implementación de convocatorias públicas para pequeños y medianos proyectos de generación de ingresos en la región circundante de esas UC. El proyecto trabajará con la población de las áreas protegidas (AP) incluidas en el proyecto, principalmente habitantes de la ribera en situación de pobreza o pobreza extrema en 16 áreas protegidas estatales (AP) en el estado de Amazonas, con aproximadamente 10.9 millones de hectáreas.</p>',
	null,
	'Programa de Asistencia Forestal +'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'546',
	'es',
	'58',
	'<p>Apoyar el fortalecimiento de la gestión comunitaria de bosques en el estado de Amazonas mediante: (i) el desarrollo de la plataforma de Ciudades Forestales para conectar a los actores forestales y apoyar las cadenas productivas de madera; y (ii) apoyo a la producción y comercialización sostenibles de madera y aceites vegetales. El proyecto funcionará en 14 municipios de Amazonas: Manaus, Itapiranga, Silves, São Sebastião do Uatumã, Apuí, Novo Aripuanã, Borba, Manicoré, Tefé, Carauari, Juruá, Jutaí, Jutaí, Lábrea y Urucará.</p>',
