INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'569',
	'es',
	'81',
	'<p>(i) Promover el manejo y la producción agroforestal en comunidades tradicionales e indígenas, a fin de construir una alternativa económica sostenible a la deforestación; (ii) brindar apoyo para el control y monitoreo territorial; y (iii) fortalecer la organización comunitaria local. El proyecto beneficiará a cerca de 720 personas indígenas que viven en la tierra indígena de Kampa do Rio Amônia (TI), representada por Apiwtxa; y otros 600 indígenas en el TI Kaxinawá-Ashaninka, junto al río Breu; y 50 comunidades en reservas extractivas de Alto Juruá; Además de las actividades de fortalecimiento del diálogo con las comunidades Ashaninka en Perú a través de programas de intercambio.</p><p>Se implementa en la región de Alto Juruá en el estado de Acre.</p>',
