INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'572',
	'es',
	'84',
	'<p>Para respaldar: (i) la implementación de Planes de Manejo Territorial y Ambiental en el Amazonas Sur de las Tierras Indígenas - IL en la cuenca del río Purus (IL Boca do Acre, IL Apurinã Km. 124 BR-317, IL Água Preta / Inari e IL Caititu ) y en la cuenca del río Madeira (IL Jiahui, IL Nove de Janeiro e IL Ipixuna) y; (ii) el desarrollo de un Plan de Manejo Territorial y Ambiental para las Tierras Indígenas Tenharim de Igarapé Preto en la cuenca del río Madeira. El proyecto apoyará ocho Tierras Indígenas (IL) en el sur de Amazonas, que cubren 1,095,169 hectáreas: ILs Boca do Acre, Apurinã Km. 124 BR-317, Água Preta / Inari, Caititu, Jiahui, Nove de Janeiro, Ipixuna y Tenharim do Igarapé Preto</p>',
