INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'787',
	'es',
	'299',
	'<p>El objetivo del proyecto es fortalecer las capacidades de las personas que dependen de los bosques de ciertas regiones seleccionadas, para participar en procesos locales, nacionales e internacionales relacionados con REDD+, así como reconocer y apoyar sus derechos de tenencia de la tierra, roles de manejo forestal y sistemas de manejo forestal tradicional.</p>',
