INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'964',
	'es',
	'476',
	'<p>La coalición Under2 está impulsada por un grupo de gobiernos estatales y regionales ambiciosos comprometidos a mantener el aumento de la temperatura global muy por debajo de 2 ° C.</p><p>La coalición está formada por más de 220 gobiernos que representan a más de 1.300 millones de personas y el 43% de la economía mundial.</p><p>The Climate Group es la Secretaría de la Coalición Under2 y trabaja con los gobiernos para acelerar la acción climática a través de tres flujos de trabajo.</p><p>2050 VÍAS</p><p>Planificación de la vía de descarbonización profunda: apoyo a los gobiernos para desarrollar planes sólidos de reducción de emisiones a medio y largo plazo (2050) en línea con los objetivos del Acuerdo Climático de París.</p><p>Acción política</p><p>Ampliando soluciones de políticas innovadoras: difundiendo las mejores políticas climáticas de hoy y apoyando a los gobiernos para que desarrollen nuevas políticas.</p><p>TRANSPARENCIA</p><p>Integración de la transparencia: apoyar a los gobiernos para que cuenten con la experiencia y los sistemas necesarios para evaluar sus emisiones con precisión, hacer un seguimiento del progreso y garantizar que las políticas sigan siendo adecuadas para cumplir con los objetivos climáticos.</p>',
	null,
	'Under2 MOU'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'965',
