INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1033',
	'fr',
	'57',
	'<p>Poursuivre et élargir les actions du programme Bolsa Floresta dans les zones protégées de l’État d’Amazonas, notamment: (i) en soutenant le développement de petites entreprises et des accords de production forestière durable; ii) la formation à la direction et le renforcement des associations locales pour la gestion de projets, l''accent étant mis sur la génération de revenus et les préoccupations environnementales et sociales; (iii) la systématisation et la diffusion du contenu, des méthodologies, des leçons apprises et des solutions innovantes; et (iv) l''exécution et la mise en œuvre d''un appel public pour des projets générateurs de revenus faibles et moyens dans la région environnante de ces UC. Le projet travaillera avec la population des zones protégées (AP) incluses dans le projet, principalement les riverains vivant dans la pauvreté ou l''extrême pauvreté dans 16 aires protégées (AP) de l''État d''Amazonas, avec environ 10,9 millions d''hectares</p>',
