INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1148',
	'fr',
	'172',
	'<p>Le projet est coordonné par le ministère brésilien de l''Environnement et prévoit l''expansion et la mise en place d''aires protégées au sein d''un système d''unités de conservation durable, réduit les menaces à la biodiversité, récupère les zones dégradées, augmente le stockage et le stockage de carbone, développe de bonnes pratiques de gestion politiques et plans de protection de l''environnement en Amazonie brésilienne. En Amazonas, l’objectif est de mettre en place une gestion intégrée du paysage afin de créer un environnement favorable aux politiques de production durable et au renforcement des capacités. En tant que partenaire clé, Conservation International-Brésil a aidé SEMA-AM et d''autres parties prenantes à atteindre ces objectifs.</p>',
