INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1182',
	'fr',
	'206',
	'<p>Renforcer la production durable, la culture et le mode de vie dans la région autonome du Kaxinawá de la rivière Humaitá, d''Arara do Igarapé Humaitá, de Gregory River et de l''Alto Purus River, dans l''État d''Acre, grâce à l''organisation et à la promotion de la chaîne de valeur des produits agroforestiers et assistance technique autochtone.</p><p>2 585 bénéficiaires autochtones des ethnies Kaxinawá, Shawadawa et Yawanawá qui habitent les quatre IL soutenus par le projet dans les terres autochtones de Kaxinawá do Rio Humaitá (TI) et Rio Gregório TI (Tarauacá / Acre); Alto Rio Purus TI (Santa Rosa do Purus et Manuel Urbano / Acre) et Arara do Igarapé Humaitá TI (Porto Walter / Acre).</p>',
	null,
	'Chaînes de valeur sur les terres autochtones à Acre'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1183',
	'fr',
	'207',
	'<p>Soutenir les initiatives entrepreneuriales de neuf institutions partenaires visant à créer des emplois et des revenus, grâce au développement durable des chaînes de production d’huile végétale, de cacao sauvage et de caoutchouc dans six municipalités de l’État d’Acre et quatre dans l’État d’Amazonas.</p><p>Les bénéficiaires comprennent des exploitations familiales et des communautés traditionnelles, telles que des extracteurs, des riverains et des peuples autochtones, apparentées à neuf institutions partenaires, notamment une coopérative de peuples autochtones et une association de femmes.</p><p>Le projet est mis en œuvre dans les municipalités de Cruzeiro do Sul, Mâncio Lima, Rodrigues Alves, Porto Walter, Tarauacá et Feijó, dans l’État d’Acre, ainsi que dans les municipalités de Pauini, Boca do Acre, Lábrea et Silves, dans l’État. d''Amazonas.</p>',
	null,
	'Chaînes de valeur des produits forestiers non ligneux'
);
