INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1263',
	'fr',
	'287',
	'<p>Le 22 avril 2016, le gouvernement de l''État du Chiapas, par l''intermédiaire du directeur général, a signé un accord avec la Commission nationale des forêts (CONAFOR) en vue de la conclusion d''un accord de coordination pour l''élaboration et la mise en œuvre de l''Initiative de réduction des émissions par le biais de programmes d''investissement ayant une portée suffisante. le district territorial de l''état de Chiapas.</p><p>Les activités spécifiques qui seront développées dans le cadre de l’Initiative de réduction des émissions ont été définies en fonction des réalités locales afin de s’attaquer aux causes de la déforestation et de la dégradation des forêts dans chaque région de l’État. Pour mettre en œuvre les activités économiques de manière durable et à long terme, il est nécessaire de mettre en place un mécanisme de financement public permettant de capter des ressources financières provenant de différentes sources nationales et internationales, publiques et privées, qui peuvent fonctionner comme un investissement ayant un impact sur le développement d''activités rurales durables et le développement des capacités des producteurs, des associations de producteurs et des communautés de l''État.</p>',
	null,
	'Initiative de réduction des émissions au Chiapas'
);
