INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1297',
	'fr',
	'321',
	'<p>Le mécanisme dédié (MDE) pour les peuples autochtones et les communautés locales au Mexique est une initiative spéciale du Programme d''investissement dans les forêts (FIP), l''un des trois programmes du Fonds stratégique pour le climat (SCF). Le MDE soutient les efforts des pays en développement pour s''attaquer aux causes sous-jacentes de la déforestation et de la dégradation des forêts et pour surmonter les obstacles qui ont entravé les efforts passés.</p><p>Le but de l''EDM est de renforcer les capacités des populations dépendantes des forêts de participer aux processus liés à REDD + aux niveaux local, national et international, dans les États sélectionnés, de manière à faciliter la détermination des déterminants de la déforestation et de la dégradation des terres forestières et de la mise en œuvre de bonnes pratiques dans différents secteurs de production, afin de catalyser le changement vers un modèle de développement rural durable à faible émission de CO2.</p>',
	null,
	'Mécanisme spécifique dédié aux peuples autochtones et aux communautés locales du Mexique (MDE)'
);
