INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1300',
	'fr',
	'324',
	'<p>Le PSE est l’instrument opérationnel de gestion des risques environnementaux et sociaux grâce auquel le Chiapas, en tant que l’un des cinq États mexicains de l’Initiative de réduction des émissions (IRE), promouvra les directives, mécanismes et procédures établis dans le Cadre de contrôle de la Banque mondiale visant à réduire les risques. gestion et d’améliorer les avantages liés aux activités REDD + qui seront développées sur son territoire et associées à la zone d’intervention définie dans le programme d’investissement enregistré dans l’IRE. Le PES sera valable 5 ans, période pendant laquelle l''IRE sera exécuté. Le Chiapas a élaboré son plan que le Mexique a proposé à la Banque mondiale en juin 2017. Après la validation, la version finale du PSE sera intégrée dans chacun des 5 États mexicains.</p>',
	null,
	'Plan d''État pour les garanties REDD + (PSE)'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1301',
	'fr',
	'325',
	'<p>Le plan d’état sur les garanties (PES pour son acronyme en espagnol) est l’instrument opérationnel de gestion des risques environnementaux et sociaux grâce auquel Campeche, en tant que l’un des 5 États mexicains participant à l’Initiative de réduction des émissions (IRE) du Fonds carbone du FCPF , fera la promotion des lignes directrices, des mécanismes et des procédures établis dans le Cadre de contrôle de sécurité de la Banque mondiale pour réduire et gérer les risques et renforcer les avantages associés aux activités REDD + qui seront développées dans le cadre des programmes d''investissement de Campeche pour l''IRE. Le PSE aura une validité de 5 ans pour coïncider avec la mise en œuvre de l''IRE. Campeche a préparé le projet de plan que le Mexique a proposé à la Banque mondiale en juin 2017. Après validation, la version finale du SPE de chacun des 5 États mexicains sera intégrée.</p>',
	null,
	'Plan d''État de sauvegarde (Plan Estatal de Salvaguardas - PSE) REDD +'
);
