INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1303',
	'fr',
	'327',
	'<p>À l’heure actuelle, on s’appuie sur le document de la stratégie REDD + d’État, mais pour sa mise en œuvre, il est nécessaire de consulter les principaux utilisateurs des forêts et de la jungle, car ce sont eux qui devront mener les activités. qui se reflètent dedans. Par conséquent, il est proposé de consulter la stratégie nationale REDD + afin de compter et de reprendre les recommandations et la validation des utilisateurs finaux. À l''avenir, avec la mise en œuvre de l''EEREDD +, la stratégie devrait améliorer l''environnement et soutenir des activités économiques respectueuses de l''environnement sans négliger l''aspect conservation des ressources naturelles, dans l''espoir de favoriser la restauration des conditions de l''écosystème afin que d''autres organismes puissent retourner dans l''environnement. les lieux d''où ils ont été déplacés.</p>',
	null,
	'Stratégie nationale REDD + pour l&#39;État du Chiapas'
);
