INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1305',
	'fr',
	'329',
	'<p>Les efforts visant à faire progresser les objectifs de développement durable comprennent plusieurs initiatives liées aux forêts, notamment:</p><ul><li>renforcer le réseau de pépinières forestières et de banques de semences;</li><li>utiliser les ressources forestières selon les critères de légalité et de durabilité;</li><li>mettre en œuvre des actions de restauration dans les zones forestières dégradées;</li><li>promouvoir une culture de prise de conscience et d''intégration de la gestion des forêts communautaires;</li><li>intégrer les zones forestières au programme de paiement pour services environnementaux;</li><li>réglementer les changements dans l''utilisation des terres et promouvoir la conservation et l''utilisation durable des terres;</li><li>réduire la superficie de l''écosystème affectée par les incendies de forêt, les ravageurs et les maladies;</li><li>augmenter la superficie de forêts sous aménagement forestier durable pour assurer la permanence des zones forestières et générer des avantages pour les propriétaires;</li><li>promouvoir la création, le maintien de plantations forestières commerciales et la diversification de la production.</li></ul>',
	null,
	'Objectifs de développement durable'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1306',
	'fr',
	'330',
	'<p>Le Plan d’action pour un élevage durable à faibles émissions dans l’État du Chiapas est un outil mis au point par le Groupe de travail sur l’élevage à faibles émissions. L’objectif du plan est de contribuer à une gestion adéquate du paysage en renforçant l’axe d’intervention de l’élevage dans l’État du Chiapas, afin de réduire les émissions de gaz à effet de serre au moyen de prototypes d’élevage durable et peu intégration des arrangements forestiers, des bonnes pratiques de gestion du bétail et de la gestion des terres.</p>',
	null,
	'Stratégie d''élevage durable visant à réduire les émissions dans l''État du Chiapas'
);
