INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1388',
	'fr',
	'412',
	'<p>D''une gestion intégrale du paysage, l''objectif du projet est de promouvoir des systèmes de production durables, sans déforestation, afin de générer de multiples avantages environnementaux. Le projet couvre une échelle nationale au niveau des politiques et des réglementations, et une autre au niveau de la gestion territoriale. Ce dernier est situé dans la région d''Ucayali (province de Padre Abad et district de Nueva Requena) et dans la région de Huánuco (province de Puerto Inca). La zone du projet couvre 2,2 millions d''hectares et comprend des zones naturelles protégées, des forêts à production permanente, des concessions forestières et autres, des propriétés rurales, des communautés autochtones et environ un tiers du territoire n''est pas catégorisé.</p><p>Le projet repose sur trois composantes:</p><p>1. Améliorer le cadre des politiques, de la planification et de la gouvernance pour une utilisation harmonisée des terres et éviter la déforestation.</p><p>2. Promouvoir des incitations et des mécanismes financiers pour une production durable, offrant aux producteurs un accès aux normes de certification et aux marchés.</p><p>3. Renforcer les capacités techniques pour réhabiliter et préserver les écosystèmes.</p>',
	null,
	null
);
