INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1398',
	'fr',
	'422',
	'<p>Initié par la GIZ, le PROFIAB appuiera le développement d’une politique nationale pour une agriculture durable et favorable à la biodiversité, qui respecte et préserve les dernières réserves de biosphère de Côte d''Ivoire. Il apportera son soutien au développement des secteurs de l''Agriculture et de l''Environnement. Le programme vise notamment à encourager la population de la région Sud-ouest à exploiter le potentiel économique et les ressources naturelles de la périphérie du Parc national de Taï (PNT) de manière durable tout en rétablissant et en améliorant leur biodiversité. Dans le cadre de ce projet, du matériel didactique sur le reboisement et la gestion forestière durable sera mis à disposition et utilisé pour réaliser des plantations forestières dans les écoles et pour d’autres activités des populations riveraines.</p>',
	null,
