INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1545',
	'id',
	'81',
	'<p>(I) Untuk mempromosikan pengelolaan dan produksi agroforestri di masyarakat tradisional dan pribumi, sehingga membangun alternatif ekonomi yang berkelanjutan untuk deforestasi; (ii) untuk memberikan dukungan untuk kontrol dan pemantauan teritorial; dan (iii) untuk memperkuat organisasi komunitas lokal. Proyek ini akan bermanfaat bagi sekitar 720 penduduk asli yang tinggal di Kampa do Rio Amônia indigenous land (TI), yang diwakili oleh Apiwtxa; dan 600 penduduk asli lainnya di Kaxinawá-Ashaninka TI di Sungai Breu; dan 50 komunitas pada cadangan ekstraktif dari Alto Juruá; selain memperkuat kegiatan dialog dengan komunitas Ashaninka di Peru melalui program pertukaran</p><p>Ini diterapkan di wilayah Alto Juruá di negara bagian Acre</p>',
