INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1546',
	'id',
	'82',
	'<p>ICMS Hijau Pará adalah instrumen Pembayaran untuk Layanan Lingkungan yang bertujuan untuk memberi kompensasi dan mendorong pemerintah kota untuk melakukan pelestarian lingkungan. ICMS Hijau Pará menonjol di Brasil karena satu-satunya yang memasukkan informasi tentang Registry Lingkungan Pedesaan dan data deforestasi dari pemerintah kota sebagai salah satu kriteria untuk penerimaan sumber daya. Antara lain, kriteria yang dievaluasi meliputi pengurangan deforestasi, keberadaan kawasan lindung, inventarisasi hutan dan kapasitas untuk melaksanakan pengelolaan lingkungan kota.</p>',
