INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1548',
	'id',
	'84',
	'<p>Untuk mendukung: (i) pelaksanaan Rencana Pengelolaan Wilayah dan Lingkungan di Amazon Selatan Tanah Adat - IL di lembah Sungai Purus (IL Boca do Acre, IL Apurinã Km 124 BR-317, IL Água Preta / Inari dan IL Caititu ) dan di lembah Sungai Madeira (IL Jiahui, IL Nove de Janeiro dan IL Ipixuna) dan; (ii) pengembangan Rencana Pengelolaan Wilayah dan Lingkungan untuk Tanah Adat Tenharim Igarapé Preto di Lembah sungai Madeira. Proyek ini akan mendukung delapan Tanah Adat (IL) di selatan Amazonas, meliputi 1.095.169 hektar: ILs Boca do Acre, Apurinã Km 124 BR-317, Água Preta / Inari, Caititu, Jiahui, Nove de Janeiro, Ipixuna dan Tenharim melakukan Igarapé Preto</p>',
