INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1549',
	'id',
	'85',
	'<p>Pusat Pemantauan Lingkungan Terpadu (CIMAM) menghadirkan pendekatan baru untuk pemantauan lingkungan di negara bagian Para, dengan tujuan untuk memajukan pengetahuan lingkungan untuk memperbaiki tindakan komando dan kontrol. Terletak di gedungnya sendiri, CIMAM memiliki infrastruktur dan peralatan untuk pemantauan hutan, iklim dan air. Diantara kegiatan utamanya adalah pemantauan rutin deforestasi secara real time untuk tindakan pemeriksaan SEMAS, dengan dukungan aparat keamanan publik. Pusat ini juga akan memiliki kemitraan dengan sektor masyarakat sipil dalam produksi statistik untuk mencari representasi pengelolaan lingkungan yang lebih baik di Pará.</p>',
