INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1557',
	'id',
	'93',
	'<p>Daftar Deforestasi Illegal Negara Bagian Pará melarang pemberian izin, otorisasi, layanan atau jenis keuntungan atau insentif publik lainnya oleh entitas Administrasi Umum Negara Bagian ke kegiatan yang berada di daerah yang secara ilegal digunduli di Negara Bagian Pará. LDI adalah alat konsultasi resmi dari State of Pará. </p><p> Ini adalah instrumen hukum untuk penyebaran kawasan penggundulan hutan ilegal di Negara Bagian, yang dikelola oleh SEMAS / PA untuk konsultasi oleh badan publik negara bagian dan masyarakat umum. Penciptaan LDI bertujuan untuk memerangi deforestasi ilegal yang dilakukan di wilayah negara bagian, mendukung produsen pedesaan yang melaksanakan kegiatan mereka sesuai dengan undang-undang lingkungan hidup dan berkolaborasi dengan komitmen Gubernur Simão Jatene selama Rio + 20 untuk mencapai zero net deforestation oleh 2020. </p><p> Dengan dikeluarkannya kebijakan baru ini, diharapkan sektor swasta (khususnya industri pengolahan makanan, bank, pembeli biji-bijian dan agribisnis) akan berkonsultasi dengan daftar embargo untuk menghindari pemasaran produk dari area yang diembargo, yang dilarang dalam undang-undang tersebut.</p>',
	null,
	null
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1558',
	'id',
	'94',
	'<p>Kesepakatan tersebut ditandatangani antara Pemerintah Negara Bagian Pará, perusahaan pengepakan daging, dan departemen kehakiman federal yang bertujuan untuk mengendalikan lingkungan dan sosial dalam rantai pasokan produksi daging di negara bagian tersebut. Perusahaan penandatangan melakukan verifikasi kriteria berikut sebelum membeli ternak dari peternakan di Pará: </p><p> 1. Didaftarkan di CAR </p><p> 2. Tidak memiliki deforestasi setelah 22 Juli 2008; </p><p> 3. Tidak menghasilkan dan menghormati batas wilayah yang diembargo oleh IBAMA; </p><p> 4. Jangan menghasilkan dan menghormati batas wilayah yang diembargo oleh SEMAS (LDI); </p><p> 5. Tidak memiliki tumpang tindih dengan Tanah Adat atau dengan Unit Konservasi, kecuali APA; </p><p> 6. Bukan pada daftar pekerjaan yang merendahkan atau serupa dengan perbudakan </p><p> 7. Perizinan untuk properti seluas lebih dari 3.000 hektar; </p><p> Sampai saat ini, dua audit independen telah dilaksanakan dan yang ketiga sedang berlangsung. Pengendalian ini diperlukan untuk memverifikasi tingkat kepatuhan kesepakatan antara para pihak dan untuk memperbaikinya, sehingga menjamin keefektifan instrumen, mengurangi deforestasi yang terkait dengan kegiatan peternakan dan memperbaiki kondisi kerja di lapangan.</p>',
	null,
	null
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1559',
	'id',
	'95',
	'<p>Untuk mendukung manajemen dan inisiatif manajemen partisipatif di Mamirauá RDS dan Amanã RDS, melalui penelitian, pengembangan dan penyebaran pengetahuan dalam topik-topik berikut: pertanian berkelanjutan, pengelolaan hutan kayu lestari, pengelolaan hutan non-kayu berkelanjutan, pendidikan lingkungan, perlindungan lingkungan, dan pemantauan . Proyek ini bermanfaat bagi komunitas lokal Mamirauá RDS dan Amanã RDS, komunitas ilmiah, pengelola kawasan lindung, dan komunitas lain yang mendapatkan manfaat dari pengetahuan yang dihasilkan dari proyek</p>',
	null,
	'Mamirauá'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1560',
