INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1664',
	'id',
	'200',
	'<p>MOU Under2 adalah kesepakatan iklim yang menyatukan pemerintah subnasional yang bersedia membuat sejumlah komitmen penting, termasuk mengurangi emisi gas rumah kaca yang setara dengan 80 sampai 95 persen di bawah tingkat tahun 1990 atau kurang dari 2 ton metrik per kapita pada tahun 2050. Para penandatangan juga berkomitmen untuk menetapkan target awalyang diperlukan untuk mendukung target pengurangan jangka panjang, berbagi teknologi, penelitian ilmiah dan praktik terbaik untuk mempromosikan efisiensi energi dan energi terbarukan dan transportasi. Under2MoU akan mendukung pemantauan, pelaporan dan verifikasi emisi penandatangan yang konsisten dan menilai dampak perubahan iklim terhadap masyarakat yang diproyeksikan.</p>',
