INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1670',
	'id',
	'206',
	'<p>Memperkuat produksi yang berkelanjutan, budaya dan cara hidup di ILs dari Kaxinawá dari Humaitá River, Arara do Igarapé Humaitá, Sungai Gregory dan Sungai Alto Purus di negara bagian Acre, melalui organisasi dan promosi rantai nilai produk agroforestri dan bantuan teknis pribumi.</p><p>2.585 penduduk asli dari Kaxinawá, Shawadawa dan Yawanawá etnis yang menghuni empat ILS didukung oleh proyek di Kaxinawá do Rio Humaitá tanah adat (TI) dan Rio Gregório TI (Tarauacá / Acre); Alto Rio Purus TI (Santa Rosa do Purus dan Manuel Urbano / Acre) dan Arara do Igarapé Humaitá TI (Porto Walter / Acre).</p>',
