INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1671',
	'id',
	'207',
	'<p>Untuk mendukung inisiatif kewirausahaan dari sembilan lembaga mitra untuk menghasilkan pekerjaan dan pendapatan, melalui pengembangan berkelanjutan rantai produksi minyak nabati, coklat liar dan karet di enam kotamadya di negara bagian Acre dan di empat di negara bagian Amazonas.</p><p>Penerima manfaat termasuk petani keluarga dan masyarakat tradisional, seperti ekstraksi, penduduk sungai dan penduduk asli, terkait dengan sembilan lembaga mitra, termasuk koperasi masyarakat adat dan asosiasi perempuan.</p><p>Proyek ini dilaksanakan di kotamadya Cruzeiro do Sul, Mâncio Lima, Rodrigues Alves, Porto Walter, Tarauacá dan Feijó, di negara bagian Acre, serta kotamadya Pauini, Boca do Acre, Lábrea dan Silves, di negara bagian dari Amazonas.</p>',
