INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1697',
	'id',
	'233',
	'<p>Program ini diselenggarakan oleh BLU sebagai lembaga di bawah Kementerian Lingkungan Hidup dan Kehutanan (KLHK). Program ini untuk mendukung kebutuhan keuangan masyarakat dan sektor swasta untuk meningkatkan pengembangan komoditas mereka.</p>',
