INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1718',
	'id',
	'254',
	'<p>Kemitraan antara perusahaan SKK-Migas dan BP-Berau dan masyarakat adat akan menghutankan kembali 1.320 ha kawasan hutan lindung di Teluk Bintuni, menggunakan pohon dan tanaman lain dengan banyak manfaat. Proyek ini dimulai pada 2017 dan akan berjalan selama 6 tahun, hingga 2022. Saat ini, penanaman di area tersebut sedang berlangsung.</p>',
	null,
	'Penanaman área pengganti ara hutan lindung - sebagai CSR'
