INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1772',
	'id',
	'308',
	'<p>Badan Antariksa Inggris melalui Program Kerjasama Internasional memprakarsai sebuah proyek untuk memperbaiki pengelolaan dan perlindungan hutan global pada 300 juta hektar hutan tropis "Hutan 2020" untuk membantu negara-negara dengan keanekaragaman hayati yang tinggi memperbaiki pengelolaan dan konservasi ekosistem hutan mereka, melalui pemantauan hutan tropis. Ecometrica adalah konsorsium internasional yang mempertemukan pakar internasional untuk memantau hutan di dunia, termasuk: Universitas Edinburgh, Universitas Leicester dan Edinburgh Carbomap. Untuk Meksiko, area jendela dipilih di dua negara bagian, Jalisco (345.531,82 ha) dan Chiapas (500.015,61 ha.), Dengan menandatangani kesepakatan kerjasama dengan mitra lokal seperti FIPRODEFO di Jalisco, PRONATURA-SUR, AMBIO dan ECOSUR di Chiapas.</p>',
