INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1793',
	'id',
	'329',
	'<p>Upaya untuk memajukan SDG mencakup beberapa inisiatif terkait hutan termasuk:</p><ul><li> memperkuat jaringan pembibitan hutan dan bank benih; </li><li> memanfaatkan sumber daya hutan berdasarkan kriteria legalitas dan keberlanjutan; </li><li> menerapkan tindakan restorasi di kawasan hutan yang terdegradasi; </li><li> mempromosikan budaya kesadaran dan integrasi pengelolaan hutan kemasyarakatan; </li><li> memasukkan kawasan hutan ke dalam program jasa lingkungan pembayaran; </li><li> mengatur perubahan penggunaan lahan dan mempromosikan konservasi dan pemanfaatan lahan secara berkelanjutan; </li><li> mengurangi luas ekosistem yang terkena dampak kebakaran hutan, hama dan penyakit; </li><li> meningkatkan luas hutan di bawah pengelolaan hutan lestari untuk memastikan keabadian kawasan hutan dan menghasilkan keuntungan bagi pemilik; </li><li> mempromosikan pembentukan, pemeliharaan hutan tanaman komersial dan diversifikasi produktif. </li></ul>',
	null,
