INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1951',
	'id',
	'487',
	'<p><em>5000 unit perumahan untuk pemukiman kembali orang-orang terlantar dari LGA Bakassi di Negara Bagian Cross River.</em></p><p>Proyek ini berusaha mengembalikan penduduk asli Bakassi yang dipindahkan ke habitat dan pekerjaan mereka yang asli dari perikanan tangkap dan pasang surut. Proyek ini memiliki perkiraan biaya sekitar $ 200.000.000,00 dan didanai oleh Program Pembangunan Bangsa Afrika, ANDP, bekerja sama dengan Pemerintah Negara Bagian Cross River. 5.000 unit rumah sedang dibangun di desa Ikpa Nkanya, Daerah Pemda Akpabuyo di Negara Bagian, dan akan menguntungkan para pengungsi Bakassi yang sebagian besar merupakan nelayan oleh pendudukan, sehingga melindungi hak dan mata pencaharian mereka.</p>',
	null,
	null
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1952',
	'id',
	'488',
	'<p><em>Naikkan 36.000 hektare loteng kayu bakar untuk meningkatkan penyediaan kayu bakar di seluruh Negara Bagian CR secara berkelanjutan.</em></p><p>Proyek ini memiliki total nilai proyek sebesar $ 20,810,000. Secara geografis terfokus di Negara Bagian Cross River, Delta dan Kaduna, Manajemen Bahan Bakar Kayu berusaha mengurangi emisi gas rumah kaca dari penggunaan kayu bakar di sektor domestik, institusional dan industri di dalam negeri melalui produksi dan pemanfaatan kayu bakar terpadu dan berkelanjutan, dan promosi energi biomassa yang berkelanjutan. teknologi yang menggunakan pendekatan berbasis output dan berbasis pasar. Proyek ini dirancang untuk secara komprehensif menangani salah satu penyebab utama deforestasi di Sungai Lintas yang merupakan pemanenan kayu bakar yang tidak berkelanjutan dan negara bagian Delta dan degradasi lahan dan penggurunan di Negara Bagian Kaduna. Proyek ini juga dirancang untuk menyeimbangkan pasokan dan permintaan kayu bakar melalui pendekatan bottom-up untuk memanfaatkan sumber daya sektor swasta dan memberikan insentif keuangan yang inklusif (pinjaman awal, skema rabat yang sesuai) dan mekanisme pasar untuk pasokan dan permintaan pasar yang berkelanjutan untuk kompor / kiln hemat energi dan kayu bakar bersertifikat .</p>',
	null,
	null
);

