INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1978',
	'pt',
	'26',
	'<p>Convênio celebrado em 2014 como IBGE e o Exército Brasileiro para a Construção da base cartográfica e a rede geodésica do Estado do Amapá, que consta de imagens de Radar nas escalas de 1:50.000 e 1:25.000. A geração de informações de qualidade e estratégicas a partir desse projeto trarão grandes beneficios para o Amapá. Para o tema de mudança de uso de solo tem fundamental importância e está servindo como base para o desenvolvimento de um monitoramento mais adequado às condições do Amapá, que se localiza em uma área de convergência intertropical, com alta cobertura de nuvens o ano inteiro, o que inviabiliza o uso de imagens de satélite.</p>',
	null,
	'Base Cartogràfica'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
