INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1988',
	'pt',
	'36',
	'<p><span class="underline">Breve descrição:</span></p><p>Em Rondônia, serão investidos 365 mil dólares, por meio do projeto <strong>Governança Climática para Rondônia</strong>, que tem os seguintes objetivos:</p><ol type="i"><li><p>(i) Apoiar a regulamentação e a implementação de políticas públicas e instrumentos estruturantes, para a redução do desmatamento e a promoção da proteção e uso sustentável das florestas;</p></li><li><p>(ii) Fomentar o engajamento do setor privado, com vistas à atração de investimentos para a implementação de modelos produtivos sustentáveis e redução do desmatamento no estado;</p></li><li><p>(iii) Mobilizar e fortalecer as capacidades da sociedade civil organizada para atuação nas temáticas relacionadas à redução do desmatamento e das emissões de gases de efeito estufa, incluindo a reativação do Fórum Estadual de Mudanças Climáticas.</p></li></ol>',
