INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1997',
	'pt',
	'45',
	'<p>Apoia o fortalecimento da gestão ambiental nos 529 municípios de nove estados da Amazônia Legal por meio de: (i) treinamento e apoio técnico; (ii) disseminação de conhecimento e informação através de redes; (iii) promoção da inovação e articulação com outros níveis de governo e sociedade em geral; no marco das políticas públicas ambientais.</p>',
	null,
	'Programa de Qualificação em Gestão Ambiental'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'1998',
	'pt',
	'46',
	'<p>O Monitoramento Ambiental dos Biomas brasileiros alcança três objetivos: (i) Desenvolvimento e implantação de sistemas de monitoramento de desmatamento nos biomas Mata Atlântica, Caatinga, Pampa e Pantanal; (ii) Cálculo de emissões de CO <sub>2</sub> para áreas desmatadas e criação de uma proposta de Emissão Florestal. Nível (FREL) para cada bioma, a ser fornecido à Convenção-Quadro das Nações Unidas sobre Mudança do Clima (UNFCCC) (iii) Desenvolvimento de plataforma para análise e visualização de vastos dados geoespaciais para beneficiar todo o território nacional através do desenvolvimento de plataformas.</p>',
	null,
	'Monitoramento Ambiental dos Biomas Brasileiros'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
