INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2020',
	'pt',
	'68',
	'<p>O projeto GEF – Cerrado Sustentável foi executado pela SEMARH, Naturatins e Ruraltins, entre os anos de 2011 e 2015. Ao todo, foram investidos U$ 9 milhões no projeto, sendo U$ 3 milhões de financiamento internacional e U$ 6 milhões de contrapartida do governo do Estado do Tocantins. As principais ações do projeto foram a criação e implementação de Unidades de Conservação no Estado e apoio a projetos produtivos de baixo impacto e alternativos ao desmatamento, como estratégia para conservação da biodiversidade e promoção do desenvolvimento sustentável do Estado. O projeto se encerrou em Junho de 2015.</p><p>Arranjo de Implementação: A iniciativa GEF Cerrado Sustentável foi coordenada pelo Ministério de Meio Ambiente. O Estado do Tocantins, por meio da SEMARH, era uma das agências executoras do projeto, junto com o Governo de Goiás e ICMBio.</p>',
	null,
	'GEF – Cerrado Sustentável'
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2021',
	'pt',
