INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2159',
	'pt',
	'207',
	'<p>Apoiar iniciativas empresariais de nove instituições parceiras para geração de emprego e renda, através do desenvolvimento sustentável das cadeias produtivas de óleo vegetal, cacau e borracha silvestre em seis municípios do estado do Acre e em quatro no estado do Amazonas.</p><p>Entre os beneficiários estão agricultores familiares e comunidades tradicionais, como extrativistas, ribeirinhos e indígenas, relacionados a nove instituições parceiras, incluindo uma cooperativa de povos indígenas e uma associação de mulheres.</p><p>O projeto é implementado nos municípios de Cruzeiro do Sul, Mâncio Lima, Rodrigues Alves, Porto Walter, Tarauacá e Feijó, no estado do Acre, além dos municípios de Pauini, Boca do Acre, Lábrea e Silves, no estado. do Amazonas.</p>',
