INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2197',
	'pt',
	'245',
	'<p>Este projeto é financiado pelo governo alemão para o Governo de Aceh para conservação da biodiversidade e atividades de proteção climática no ecossistema de Gunung Leuseur. Baseado na Carta do Ministério das Finanças no.S-103 / MK.7 / 2015 de 14 de julho de 2015 de EUR 4,194,714 Ou Rp.67.115.424.000 </p><p> Com detalhes: </p><p> 2016 - 300 000 euros </p><p> 2017 - EUR 1.429.714 </p><p> 2018 - 1.621.000 euros </p><p> 2019 - 844 000 euros </p><p> O projeto centra-se no distrito de South Aceh, na regiao de Kota Sebulussalam e Aceh Singkil, ou no distrito diretamente adjacente ao Parque Nacional Gunung Leuser Aceh.</p>',
