INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2257',
	'pt',
	'305',
	'<p>No âmbito da Conferência das Partes da Convenção sobre Biodiversidade (COP13) realizada em dezembro de 2016 em Cancún, Quintana Roo, Secretária de Meio Ambiente e Recursos Naturais de Campeche (SEMARNATCAM) da Campeche, em coordenação com a Comissão Nacional de Conhecimento e Uso da Biodiversidade (CONABIO) e dos Governos Locais para a Sustentabilidade (ICLEI) coorganizou a 5ª Cúpula Global de Biodiversidade das Cidades e os governos subnacionais. Como resultado da reunião, os líderes dos governos subnacionais estabeleceram um intativo para construir uma nova abordagem cooperativa para trocar experiências e melhores práticas e estabelecer um plano de ação para que os governos participantes atinjam os Objetivos da Biodiversidade de Aichi.</p>',
