INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2259',
	'pt',
	'307',
	'<p>O Programa Especial Concorrente das Regiões Milpa Maya e Área de Influência do Estado de Yucatán reconhece os fatores históricos, ambientais, sociais, econômicos, culturais e legais que afetam o milpa como forma de produção e reprodução da comunidade maia no Estado do Yucatán. Identifica e desenvolve propostas para melhorar as condições de vida e aumentar os níveis de bem-estar das famílias maia. O Programa Milpa da Região de Milpera e a área de influência tem como objetivo garantir e promover o uso responsável e eficiente dos serviços ecossistêmicos que os ecossistemas fornecem à sociedade e cujos benefícios são distribuídos o mais amplamente possível para o bem-estar humano.</p>',
