INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2286',
	'pt',
	'334',
	'<p>A Associação Nacional de Autoridades Ambientais do Estado (ANAAE), que preside o Governo de Campeche através da Secretaria de Meio Ambiente e Recursos Naturais de Campeche (SEMARNATCAM), está promovendo um novo modelo regional de cooperação através da assinatura da Carta de Tumbira: Um Chamado para Ação em maio de 2017, na cidade de Manaus, Brasil. O objetivo do chamado é fortalecer a cooperação entre as instituições latino-americanas em esforços para promover os Objetivos de Desenvolvimento Sustentável. Este modelo representa um exemplo de liderança e trabalho em equipe para motivar e direcionar o investimento para o uso estratégico e responsável dos recursos naturais na América Latina. Iniciou-se a novos membros da aliança, inclusive do setor privado.</p>',
