);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2296',
	'pt',
	'344',
	'<p>O projeto melhora a conectividade entre o Parque Nacional do Rio Abiseo e a Floresta Protegida do Alto Mayo e faz parte do Corredor de Conservação Abiseo Condor - Kukutú. O projeto envolve registro legal, controle e vigilância, atividades econômicas não madeireiras, educação e conscientização, fortalecimento da comunidade e fortalecimento organizacional, promoção e comunicação, ligação internacional e certificação internacional. O projeto está em execução. Verificou-se no âmbito do VCS.</p>',
