INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2301',
	'pt',
	'349',
	'<p>A concessão na bacia hidrográfica Los Amigos foi criada em julho de 2001. O projeto começou em 2008 e atualmente continua a operar ativamente. O objetivo é projetar uma Estação Biológica em Madre de Dios para treinar profissionais e promover pesquisas sobre recursos biológicos e seu meio ambiente.</p>',
