INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2303',
	'pt',
	'351',
	'<p>O projeto está localizado dentro da Concessão de Conservação Alto Huayabamba. As atividades para prevenir o desmatamento são: atividades produtivas sustentáveis, educação e governança aprimorada. As famílias beneficiárias pertencem aos distritos do Amazonas, La Libertad e San Martín. O projeto busca validação de VCS e CCB.</p>',
