INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2333',
	'pt',
	'381',
	'<p>Pro Amazonas is an executing unit of the Amazonas Regional Government that allows the implementation of investment projects in an agile manner. It executes resources on tourism, road infrastructure and sanitation in Amazonas within the framework of an agreement with the Japan International Cooperation Agency. Its objective is to establish the bases of rural development in the Region, thus contributing to equitable and inclusive rural economic and social development and the improvement of the population''s standard of living.</p>',
