INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2350',
	'pt',
	'398',
	'<p><em>Atividades REDD + por 1,3 milhão de hectares da Cordilheira Azul Nacional.</em></p><p>O projeto inclui gerenciamento de infra-estrutura, estabelecimento de áreas protegidas viáveis, estabilização do uso da terra e posse na zona tampão, trabalho com comunidades e funcionários locais e coordenação com os governos regionais e nacionais para desenvolver estratégias de desenvolvimento compatíveis com a biodiversidade do Natural Área protegida. O projeto recebeu financiamento de várias fontes, incluindo o Field Museum, a Agência dos Estados Unidos para o Desenvolvimento Internacional (USAID), a Fundação Gordon e Betty Moore, a Fundação MacArthur, a Fundação Exelon Frankel Family Foundation e a Agência Espanhola de Cooperação Internacional (AECID) Os padrões de VCS e CCB foram utilizados.</p>',
	null,
	null
);
INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2351',
