INSERT INTO partnership_translate (id, language_code, partnership_id, description, initiative_status_details, name) VALUES (
	'2357',
	'pt',
	'405',
	'<p>O projeto busca promover o gerenciamento sustentável da floresta seca ACR Salitral Huarmaca através de seis atividades principais: i) construção de infra-estrutura e equipamentos para gerenciamento, controle e monitoramento; ii) elaboração de documentos normativos e de planejamento do ACR e assistência técnica para a concepção e desenvolvimento de software de planejamento; iii) o desenvolvimento de um sistema eficiente de controle, monitoramento e vigilância; iv) elaboração de um plano de gestão para a proteção da flora e da fauna; v) desenvolvimento de conhecimentos e compromissos adequados da população para participação na conservação; vi) um compromisso do governo local para apoiar a conservação e proteção do ACR.</p>',
