INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '32', 'Estatuto', 'Altera os Anexos V e VI da Lei nº. 1.073, de 02 de abril de 2007, que dispõe sobre a organização do Poder Executivo do Estado do Amapá e dá outras providências', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '33', 'Estatuto', 'Dispõe sobre alterações na Lei nº 1.077, de 02 de abril de 2007, que criou o Instituto Estadual de Florestas do Amapá - IEF/AP e dá outras providências. ', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '34', 'Estatuto', 'Dispõe sobre as terras públicas e devolutas do Estado, disciplina sua ocupação e dá outras providências', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '35', 'Plano', 'Plano de Manejo da Floresta  do Amapá', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '36', 'Plano', 'Plano Anual de Outorga Florestal (PAOF)', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '37', 'Plano', 'Plano de Prevenção e Controle do Desmatamento e Queimadas (PPCDAP)', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '38', 'Estatuto', 'Regulamenta o Art. 220, § 1º e § 2º da Constituição Estadual, institui o Conselho Estadual de Meio Ambiente do Estado do Amazonas – CEMAAM e dá outras providências.', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '39', 'Estatuto', 'Institui a Política Estadual sobre Mudanças Climáticas, Conservação Ambiental e Desenvolvimento Sustentável do Amazonas', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '40', 'Estatuto', 'Dispoe sobre a Secretaria de Estado Do Meio Ambiente e Desenvolvimento Sustentavel - SDS, definindo suas finalidades, competências e estrutura organizacional', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '41', 'Estatuto', 'Dispoe sobre a criação da Unidade Gestora do Centro Estadual de Mudanças Climáticas e do Centro Estadual de Unidades de Conservação', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '42', 'Estatuto', 'Institui o Macrozoneamento Ecológico - Econômico do Estado do Amazonas - MZEE.', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '43', 'Estatuto', 'Dispoe sobre a Criação do Conselho de Desenvolvimento Sustentavel de Povos e Comunidades Tradicionais Do Estado Do Amazonas CDSPCT/AM', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '44', 'Estatuto', 'Dispoe sobre as concessões florestais nas unidades de conservação de uso sustentável denominadas Florestas Estaduais-Florestas, objetivando o uso múltiplo sustentável dos recursos florestais e serviços ambientais, a pesquisa científica e o desenvolvimento sustentável de omunidades tradicionais', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '45', 'Estatuto', 'Instituti o Zoneamento Ecológico-Econômico - ZEE da Sub-Região do Purus no Estado do Amazonas, estabelece diretrizes de uso e ocupação do solo', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '46', 'Estatuto', 'Institui a Política do Estado do Amazonas de Serviços Ambientais e o Sistema de Gestão dos Serviços Ambientais, cria o Fundo Estadual de Mudanças Climáticas, Conservação Ambiental e Serviços Ambientais', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '47', 'Estatuto', 'Institui a Política Econômica Ambiental do Estado do Amazonas para o Desenvolvimento Sustentável, denominada “Matriz Econômica-Ambiental do Amazonas”', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '48', 'Estatuto', 'Estabelece a Política Estadual de Regularização Ambiental, dispõe sobre o Cadastro Ambiental Rural - CAR, o Sistema de Cadastro Ambiental Rural - SISCAR-AM, o Programa de Regularização Ambiental - PRA, no Estado do Amazonas', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '49', 'Estatuto', 'Disciplina a execuçao do artigo 220 da Constituiçai Estadual, que institui o Conselho Estadual de Meio Ambiente do Estado do Amazonas - CEMAAM e dispoe sobre o Fundo Estadual de Medio Ambiente -FEMA ', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '50', 'Decreto', 'Institui o fórum Amazonense de mudanças climáticas Globais, Biodiversidade e serviços Ambientais', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '51', 'Estatuto', 'Institui o Código de Proteção do Meio Ambiente e dispõe sobre o Sistema Estadual do Meio Ambiente e o uso adequado dos recursos naturais do Estado do Maranhão.', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '52', 'Estatuto', 'Regulamenta o art. 241 da Constituição do Estado do Maranhão, o Capítulo III, Seção VII da Lei Estadual nº 5.405, de 08 de abril de 1992, o Capítulo II, Seção VIII do Decreto Estadual nº 13.494, de 12 de novembro de 1993, e institui o Sistema Estadual de Unidades de Conservação da Natureza do Maranhão e dá outras providências.', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '53', 'Estatuto', 'Regulamenta a Compensação Ambiental no âmbito do Estado do Maranhão', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '54', 'Estatuto', 'Institui o Programa de Adequação Ambiental de Propriedade e Atividade Rural', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '55', 'Estatuto', 'Institui o Macrozoneamento EcológicoEconômico do Estado do Maranhão ', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '56', 'Estatuto', 'Institui a Política Estadual de Assistência Técnica e Extensão Rural para a Agricultura Familiar e Reforma Agrária -PEATER/ MA e o Programa Estadual de Assistência Técnica e Extensão Rural para a Agricultura Familiar e Reforma Agrária - PROATER/ MA no Estado do Maranhão', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '57', 'Estatuto', 'Institui o Programa Maranhão Verde, destinado a fomentar e desenvolver projetos voltados para apoio à conservação e recuperação ambiental', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '58', 'Plano', 'Plano de Ação para a Prevenção e o Controle do Desmatamento e das Queimadas no Estado do Maranhão (PPCD-MA)', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '59', 'Estatuto', 'Dispõe sobre a Política Florestal do Estado de Mato Grosso ', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '60', 'Estatuto', 'Cria o Programa Mato-grossense de Regularização Ambiental Rural – MT LEGAL, disciplina as etapas do Processo de Licenciamento Ambiental de Imóveis Rurais', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '61', 'Estatuto', 'Cria o Sistema Estadual de Redução de Emissões por Desmatamento e Degradação Florestal, Conservação, Manejo Florestal Sustentável e Aumento dos Estoques de Carbono Florestal - REDD+ no Estado de Mato Grosso', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '62', 'Estatuto', 'Institui a Política Estadual de Mudanças Climáticas', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '63', 'Decreto', 'Institui o Plano Estadual de Agricultura de Baixo Carbono - Plano ABC-MT, no âmbito do "Plano Setorial de Mitigação e de Adaptação às Mudanças Climáticas para a Consolidação de uma Economia de Baixa Eissão de Carbono na Agricultura", com o objetivo de promover a mitigação de emissões de GEE provenientes da agropecuária no MT', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '64', 'Decreto', 'Institui o Plano de Ação para Prevenção e Controle do Desmatamento e Incêndios Florestais no Estado de Mato Grosso - PPCDIF/MT', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '65', 'Decreto', 'Regulamenta a Lei Complementar nº 233, de 21 de dezembro de 2005 no que diz respeito aos procedimentos de elaboração, análise e acompanhamento dos Planos de Manejo Florestal Sustentável Madeireiro no Estado de Mato Grosso.', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '66', 'Decreto', 'Dispõe sobre a composição do Conselho Estadual do Meio Ambiente - CONSEMA, disciplina a eleição de seus membros', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '67', 'Decreto', 'Dispõe sobre a instituição e regulamentação do Conselho Gestor do Sistema Estadual de REDD+', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '68', 'Decreto', 'Dispõe sobre a identificação das áreas prioritárias dentro do Estado de Mato Grosso para fins de Compensação de Reserva Legal entre Estados, bem como os procedimentos', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '69', 'Decreto', 'Regulamenta o art. 8º da Lei Complementar nº 343, de 24 de dezembro de 2008, instituindo a Autorização Provisória de Funcionamento de Atividade Rural - APF, no âmbito do procedimento da Licença Ambiental Única, bem como a forma de comunicação dos atos administrativos da Secretaria de Estado de Meio Ambiente - SEMA', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '70', 'Decreto', 'Cria o Comitê Estadual de Gestão do Programa de Educação Ambiental na Agricultura Familiar ­PEAAF', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '71', 'Decreto', 'Aprova o Regimento Interno da Secretaria de Estado de Meio Ambiente - SEMA', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '72', 'Decreto', 'Institui a Comissão Estadual do Zoneamento Socioeconômico Ecológico - CEZSEE/MT', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '73', 'Decreto', 'Dispõe sobre a instituição do Comitê Estratégico de Gestão do Programa Global REDD Early Movers - REM, no Estado de Mato Grosso - CEGREM/MT', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '74', 'De outros', 'Produzir, Conservar e Incluir: Estrategia de Mato Grosso para a Reduçao do aquecimiento global ', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '75', 'Estatuto', 'Dispõe sobre a Política Estadual do Meio Ambiente ', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '76', 'Estatuto', 'Institui o Macrozoneamento Ecológico-Econômico do Estado do Pará', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '77', 'Estatuto', 'Dispõe sobre a estrutura da Administração Pública do Poder Executivo Estadual', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '78', 'Decreto', 'Dispõe sobre o Cadastro Ambiental Rural - CAR-PA', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '79', 'Decreto', 'Institui o Plano de Prevenção, Controle e Alternativas ao Desmatamento do Estado do Pará - PPCAD/PA', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '80', 'Decreto', 'Dispõe sobre a manutenção, recomposição, condução da regeneração natural, compensação e composição da área de Reserva Legal de imóveis rurais no Estado do Pará', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '81', 'Decreto', 'Institui o Programa de Municípios Verdes - PMV no âmbito do Estado do Pará ', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '82', 'Decreto', 'Cria o Programa de Regularização Ambiental dos Imóveis Rurais do Estado do Pará - PRA/PA', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '83', 'Estatuto', 'Dispõe sobre a criação do Sistema Estadual de Desenvolvimento Ambiental de Rondônia-SEDAR e seus instrumentos, estabelece medidas de proteção e melhoria da qualidade de meio ambiente, define a Polícia Estadual de Desenvolvimento Ambiental, cria o Fundo Especial de Desenvolvimento Ambiental-FEDARO e o Fundo Especial de Reposição Florestal-FEREF', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '84', 'Estatuto', 'Dispõe sobre a estruturação organizacional e o funcionamento da Administração Pública Estadual, extingue, incorpora órgãos do Poder Executivo Estadual e dá outras providências', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '85', 'Decreto', 'Regulamenta a Lei nº 547, de 30 de dezembro de 1993, que dispõe sobre proteção, recuperação, controle, fiscalização e melhoria de qualidade do meio ambiente no Estado de Rondônia', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '86', 'Decreto', 'Dispõe sobre a Gestão Florestal do Estado de Rondônia e dá outras providências', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '87', 'Decreto', 'Institui o Cadastro de Exploradores e Consumidores de Produtos Florestais do Estado de Rondônia – CEPROF-RO, o Sistema de Comercialização e Transporte de Produtos Florestais do Estado de Rondônia SISFLORA-RO, seus documentos operacionais', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '88', 'Decreto', 'Introduz artigo no Decreto n° 7903, de 1° de julho de 1997, que regulamentou a Lei n° 547, de 30 de dezembro de 1993', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '89', 'Decreto', 'Dispõe sobre o Sistema Estadual de Unidades de Conservação da Natureza de Rondônia - SEUC/RO ', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '90', 'Decreto', 'Dispõe sobre o Programa de Regularização Ambiental dos Imóveis Rurais do Estado de Rondônia e dá outras providências', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '91', 'Plano', 'Plano de Prevenção, Controle e Alternativas Sustentáveis ao Desmatamento em Rondônia', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '92', 'Estatuto', 'Cria o Instituto de Terras e Colonização de Roraima e dá outras providências', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '93', 'Estatuto', 'Institui o Código de Proteção ao Meio Ambiente para a Administração da Qualidade Ambiental, Proteção, Controle e Desenvolvimento do Meio Ambiente e uso adequado dos Recursos Naturais do Estado de Roraima', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '94', 'Estatuto', 'Cria o Centro de Geotecnologia, Cartografia e Planejamento Territorial - CGPTERR, como Unidade Administrativa Desconcentrada da Secretaria de Estado do Planejamento e Desenvolvimento - SEPLAN', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '95', 'Estatuto', 'Institui o Sistema de Planejamento e Ordenamento Territorial do Estado de Roraima', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '96', 'Estatuto', 'Dispõe sobre a transformação da Fundação Estadual do Meio Ambiente, Ciência e Tecnologia do Estado de Roraima – FEMACT-RR, e do Instituto de Desenvolvimento Florestal do Estado de Roraima – IDEFER', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '97', 'Estatuto', 'Dispõe sobre a Política Fundiária Rural e de Regularização Fundiária Rural do Estado de Roraima', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '98', 'Estatuto', 'Dispõe sobre a Política Florestal do Estado do Tocantins', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '99', 'Estatuto', 'Dispõe sobre a Política Estadual de Recursos Hídricos', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '100', 'Estatuto', 'Institui instrumentos de compensação e modos de recomposição de áreas de Reserva Legal', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '101', 'Estatuto', 'Institui o Sistema Estadual de Unidades de Conservação da Natureza - SEUC', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '102', 'Estatuto', 'Dispõe sobre o Conselho Estadual do Meio Ambiente do Tocantins - COEMA/TO', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '103', 'Estatuto', 'Institui a Política Estadual sobre Mudanças Climáticas, Conservação Ambiental e Desenvolvimento Sustentável do Tocantins', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '104', 'Estatuto', 'Institui o Zoneamento Ecológico-Econômico do Estado do Tocantins - ZEE', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '105', 'Estatuto', 'Institui o Programa de Adequação Ambiental de Propriedade e Atividade Rural - TO-LEGAL', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '106', 'Decreto', 'Institui o Plano Estadual de Mitigação e de Adaptação às Mudanças Climáticas para a Consolidação de uma Economia de Baixa Emissão de Carbono na Agricultura – Plano ABC-TO', null);
INSERT INTO law_translate (language_code, law_id, law_type, name, summary) VALUES ('pt', '107', 'Plano', 'Plano de Ação para Prevenção e Controle do Desmatamento e Queimadas do Estado do Tocantins', null);
