DROP TABLE IF EXISTS region CASCADE;
CREATE TABLE region (
 id SERIAL PRIMARY KEY,
 name VARCHAR(32),
 coat_of_arms_url VARCHAR(2083),
 flag_url VARCHAR(2083)
);

DROP TABLE IF EXISTS nation CASCADE;
CREATE TABLE nation (
 id SERIAL PRIMARY KEY,
 name VARCHAR(32),
